import React, { useState, useEffect } from 'react';
import { Star, Users } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { officialsAPI } from '../services/api';

const Homepage = () => {
  const navigate = useNavigate();
  const [officials, setOfficials] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterCategory, setFilterCategory] = useState('all'); // New filter for categories
  const [filterParty, setFilterParty] = useState('all');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchOfficials();
  }, []);

  const fetchOfficials = async () => {
    try {
      setLoading(true);
      const response = await officialsAPI.getAll();
      // Use mock data if API doesn't return data
      const mockOfficials = [
        {
          id: 1,
          name: "Priya Reddy",
          position: "MLA - Bangalore South",
          party: "Indian National Congress",
          constituency: "Bangalore South",
          tenure: "2018–Present",
          dynastyStatus: "Third Generation",
          score: 87,
          image: "https://api.dicebear.com/7.x/avataaars/svg?seed=Priya",
          approvals: 2847,
          disapprovals: 423,
          promises: 24,
          completed: 18,
          inProgress: 4,
          broken: 2,
          lastUpdated: "2 hours ago",
          sources: { myneta: true, eci: true, twitter: true, news: 15, govt: 8 },
          discussions: 156,
          profileDetails: {
            education: { value: "MBA from IIM Bangalore", sourceUrl: "https://myneta.info/priya-reddy" },
            assets: { value: "₹2.5 Crores", sourceUrl: "https://eci.gov.in/affidavit/priya-reddy" },
            liabilities: { value: "₹45 Lakhs", sourceUrl: "https://eci.gov.in/affidavit/priya-reddy" },
            criminalCases: { value: "0 Cases", sourceUrl: "https://myneta.info/priya-reddy" },
            age: { value: "42 years", sourceUrl: "https://myneta.info/priya-reddy" },
            contactEmail: { value: "priya.reddy@karnataka.gov.in", sourceUrl: "https://karnataka.gov.in/officials" }
          }
        },
        {
          id: 2,
          name: "Anil Kumar",
          position: "Mayor - BBMP Ward 42",
          party: "BJP",
          constituency: "BBMP Ward 42",
          tenure: "2020–Present",
          dynastyStatus: "Political Family",
          score: 68,
          image: "https://api.dicebear.com/7.x/avataaars/svg?seed=Anil",
          approvals: 1823,
          disapprovals: 891,
          promises: 18,
          completed: 10,
          inProgress: 5,
          broken: 3,
          lastUpdated: "1 day ago",
          sources: { myneta: true, eci: true, twitter: true, news: 12, govt: 6 },
          discussions: 89,
          profileDetails: {
            education: { value: "BA from Bangalore University", sourceUrl: "https://myneta.info/anil-kumar" },
            assets: { value: "₹1.8 Crores", sourceUrl: "https://eci.gov.in/affidavit/anil-kumar" },
            liabilities: { value: "₹30 Lakhs", sourceUrl: "https://eci.gov.in/affidavit/anil-kumar" },
            criminalCases: { value: "1 Case (Pending)", sourceUrl: "https://myneta.info/anil-kumar" },
            age: { value: "48 years", sourceUrl: "https://myneta.info/anil-kumar" },
            contactEmail: { value: "anil.kumar@bbmp.gov.in", sourceUrl: "https://bbmp.gov.in/officials" }
          }
        },
        {
          id: 3,
          name: "Meena Raj",
          position: "Urban Development Minister - Bangalore Central",
          party: "JD(S)",
          constituency: "Bangalore Central",
          tenure: "2019–Present",
          dynastyStatus: "Self-Made",
          score: 82,
          image: "https://api.dicebear.com/7.x/avataaars/svg?seed=Meena",
          approvals: 3201,
          disapprovals: 567,
          promises: 31,
          completed: 22,
          inProgress: 7,
          broken: 2,
          lastUpdated: "5 hours ago",
          sources: { myneta: true, eci: true, twitter: true, news: 21, govt: 14 },
          discussions: 203,
          profileDetails: {
            education: { value: "MSc Urban Planning", sourceUrl: "https://myneta.info/meena-raj" },
            assets: { value: "₹3.2 Crores", sourceUrl: "https://eci.gov.in/affidavit/meena-raj" },
            liabilities: { value: "₹60 Lakhs", sourceUrl: "https://eci.gov.in/affidavit/meena-raj" },
            criminalCases: { value: "0 Cases", sourceUrl: "https://myneta.info/meena-raj" },
            age: { value: "45 years", sourceUrl: "https://myneta.info/meena-raj" },
            contactEmail: { value: "meena.raj@karnataka.gov.in", sourceUrl: "https://karnataka.gov.in/officials" }
          }
        }
      ];
      setOfficials(response.data?.length > 0 ? response.data : mockOfficials);
    } catch (error) {
      console.error('Error fetching officials:', error);
      // Use mock data on error
      setOfficials([
        {
          id: 1,
          name: "Priya Reddy",
          position: "MLA - Bangalore South",
          party: "Indian National Congress",
          constituency: "Bangalore South",
          tenure: "2018–Present",
          dynastyStatus: "Third Generation",
          score: 87,
          image: "https://api.dicebear.com/7.x/avataaars/svg?seed=Priya",
          approvals: 2847,
          disapprovals: 423,
          promises: 24,
          completed: 18,
          inProgress: 4,
          broken: 2,
          lastUpdated: "2 hours ago",
          sources: { myneta: true, eci: true, twitter: true, news: 15, govt: 8 },
          discussions: 156,
          profileDetails: {
            education: { value: "BA Political Science, LLB", sourceUrl: "https://myneta.info/priya-reddy" },
            assets: { value: "₹5.2 Crores", sourceUrl: "https://eci.gov.in/affidavit/priya-reddy" },
            liabilities: { value: "₹1.2 Crores", sourceUrl: "https://eci.gov.in/affidavit/priya-reddy" },
            criminalCases: { value: "0 Cases", sourceUrl: "https://myneta.info/priya-reddy" },
            age: { value: "42 years", sourceUrl: "https://myneta.info/priya-reddy" },
            contactEmail: { value: "priya.reddy@karnataka.gov.in", sourceUrl: "https://karnataka.gov.in/mla" }
          }
        },
        {
          id: 2,
          name: "Anil Kumar",
          position: "Mayor - BBMP Ward 42",
          party: "BJP",
          constituency: "BBMP Ward 42",
          tenure: "2020–Present",
          dynastyStatus: "Self-Made",
          score: 68,
          image: "https://api.dicebear.com/7.x/avataaars/svg?seed=Anil",
          approvals: 1823,
          disapprovals: 891,
          promises: 18,
          completed: 10,
          inProgress: 5,
          broken: 3,
          lastUpdated: "1 day ago",
          sources: { myneta: true, eci: true, twitter: true, news: 12, govt: 6 },
          discussions: 89,
          profileDetails: {
            education: { value: "BA from Bangalore University", sourceUrl: "https://myneta.info/anil-kumar" },
            assets: { value: "₹1.8 Crores", sourceUrl: "https://eci.gov.in/affidavit/anil-kumar" },
            liabilities: { value: "₹30 Lakhs", sourceUrl: "https://eci.gov.in/affidavit/anil-kumar" },
            criminalCases: { value: "1 Case (Pending)", sourceUrl: "https://myneta.info/anil-kumar" },
            age: { value: "48 years", sourceUrl: "https://myneta.info/anil-kumar" },
            contactEmail: { value: "anil.kumar@bbmp.gov.in", sourceUrl: "https://bbmp.gov.in/officials" }
          }
        },
        {
          id: 3,
          name: "Meena Raj",
          position: "Urban Development Minister - Bangalore Central",
          party: "JD(S)",
          constituency: "Bangalore Central",
          tenure: "2019–Present",
          dynastyStatus: "Self-Made",
          score: 82,
          image: "https://api.dicebear.com/7.x/avataaars/svg?seed=Meena",
          approvals: 3201,
          disapprovals: 567,
          promises: 31,
          completed: 22,
          inProgress: 7,
          broken: 2,
          lastUpdated: "5 hours ago",
          sources: { myneta: true, eci: true, twitter: true, news: 21, govt: 14 },
          discussions: 203,
          profileDetails: {
            education: { value: "MSc Urban Planning", sourceUrl: "https://myneta.info/meena-raj" },
            assets: { value: "₹3.2 Crores", sourceUrl: "https://eci.gov.in/affidavit/meena-raj" },
            liabilities: { value: "₹60 Lakhs", sourceUrl: "https://eci.gov.in/affidavit/meena-raj" },
            criminalCases: { value: "0 Cases", sourceUrl: "https://myneta.info/meena-raj" },
            age: { value: "45 years", sourceUrl: "https://myneta.info/meena-raj" },
            contactEmail: { value: "meena.raj@karnataka.gov.in", sourceUrl: "https://karnataka.gov.in/officials" }
          }
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const filteredOfficials = officials.filter(official => {
    const matchesSearch = official.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         official.constituency?.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         official.party?.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesParty = filterParty === 'all' || official.party === filterParty;
    
    // Category filters based on spreadsheet
    let matchesCategory = true;
    if (filterCategory === 'mlas') {
      matchesCategory = official.position?.toLowerCase().includes('mla');
    } else if (filterCategory === 'knowledgeable') {
      // Filter by education level - assuming higher education = knowledgeable
      matchesCategory = official.profileDetails?.education?.value?.toLowerCase().includes('msc') ||
                       official.profileDetails?.education?.value?.toLowerCase().includes('mba') ||
                       official.profileDetails?.education?.value?.toLowerCase().includes('llb');
    } else if (filterCategory === 'dynastic') {
      matchesCategory = official.dynastyStatus && official.dynastyStatus !== 'Self-Made';
    } else if (filterCategory === 'most-active') {
      // Filter by number of discussions/promises activity
      matchesCategory = official.discussions > 100 || official.promises > 20;
    } else if (filterCategory === 'mayor') {
      matchesCategory = official.position?.toLowerCase().includes('mayor');
    } else if (filterCategory === 'minister') {
      matchesCategory = official.position?.toLowerCase().includes('minister');
    }
    
    return matchesSearch && matchesParty && matchesCategory;
  });

  return (
    <div style={{ minHeight: '100vh', background: '#f9fafb' }}>
      <header style={{ maxWidth: '1280px', margin: '0 auto', padding: '1.5rem', marginTop: '1.5rem' }}>
        <div className="card">
          <h2 style={{ fontSize: '1.25rem', fontWeight: 600, marginBottom: '0.5rem' }}>
            Phase 1 — Profiles Foundation (Bangalore)
          </h2>
          <p style={{ color: '#6b7280', marginTop: '0.5rem' }}>
            Aggregate verified public data (MyNeta, ECI, Twitter, news, govt sources) to build authoritative profiles of elected officials. The platform is a forum that lets users compare government vs elected officials, track promises, discuss issues, and measure performance over time.
          </p>
        </div>

        {/* Category Filters - As per spreadsheet */}
        <div className="card" style={{ marginTop: '1rem' }}>
          <h3 style={{ fontWeight: 600, marginBottom: '1rem', fontSize: '0.875rem', color: '#6b7280', textTransform: 'uppercase', letterSpacing: '0.05em' }}>
            Filter by Category
          </h3>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '0.5rem' }}>
            <button
              className={`btn ${filterCategory === 'all' ? 'btn-primary' : 'btn-outline'}`}
              onClick={() => setFilterCategory('all')}
              style={{ fontSize: '0.875rem', padding: '0.5rem 1rem' }}
            >
              All
            </button>
            <button
              className={`btn ${filterCategory === 'mlas' ? 'btn-primary' : 'btn-outline'}`}
              onClick={() => setFilterCategory('mlas')}
              style={{ fontSize: '0.875rem', padding: '0.5rem 1rem' }}
            >
              MLAs
            </button>
            <button
              className={`btn ${filterCategory === 'knowledgeable' ? 'btn-primary' : 'btn-outline'}`}
              onClick={() => setFilterCategory('knowledgeable')}
              style={{ fontSize: '0.875rem', padding: '0.5rem 1rem' }}
            >
              Knowledgeable
            </button>
            <button
              className={`btn ${filterCategory === 'dynastic' ? 'btn-primary' : 'btn-outline'}`}
              onClick={() => setFilterCategory('dynastic')}
              style={{ fontSize: '0.875rem', padding: '0.5rem 1rem' }}
            >
              Dynastic
            </button>
            <button
              className={`btn ${filterCategory === 'most-active' ? 'btn-primary' : 'btn-outline'}`}
              onClick={() => setFilterCategory('most-active')}
              style={{ fontSize: '0.875rem', padding: '0.5rem 1rem' }}
            >
              Most Active
            </button>
            <button
              className={`btn ${filterCategory === 'mayor' ? 'btn-primary' : 'btn-outline'}`}
              onClick={() => setFilterCategory('mayor')}
              style={{ fontSize: '0.875rem', padding: '0.5rem 1rem' }}
            >
              Departments (Mayor)
            </button>
            <button
              className={`btn ${filterCategory === 'minister' ? 'btn-primary' : 'btn-outline'}`}
              onClick={() => setFilterCategory('minister')}
              style={{ fontSize: '0.875rem', padding: '0.5rem 1rem' }}
            >
              MPs/Council Members
            </button>
          </div>
        </div>
      </header>

      <div style={{ maxWidth: '1280px', margin: '0 auto', padding: '1rem' }}>
        <main>
          {loading ? (
            <div style={{ textAlign: 'center', padding: '3rem' }}>
              <p style={{ color: '#6b7280' }}>Loading officials...</p>
            </div>
          ) : filteredOfficials.length === 0 ? (
            <div className="card" style={{ textAlign: 'center', padding: '3rem' }}>
              <p style={{ color: '#6b7280' }}>No officials found. Try adjusting your filters.</p>
            </div>
          ) : (
            <>
              <section style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1.5rem' }}>
                {filteredOfficials.map((official) => (
                    <div 
                      key={official.id}
                      className="card"
                      onClick={() => navigate(`/profile/${official.id}`, { state: { official } })}
                      style={{ cursor: 'pointer', transition: 'all 0.2s' }}
                      onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-4px)'}
                      onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
                    >
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start', marginBottom: '1rem' }}>
                        <div>
                          <h3 style={{ fontSize: '1rem', fontWeight: 600 }}>{official.name}</h3>
                          <div style={{ fontSize: '0.75rem', color: '#6b7280', marginTop: '0.25rem' }}>
                            {official.position} • {official.constituency}
                          </div>
                        </div>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                          <Star size={16} style={{ color: '#eab308' }} fill="#eab308" />
                          <span style={{ fontWeight: 600 }}>{official.score}</span>
                        </div>
                      </div>

                      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
                        <img 
                          src={official.image} 
                          alt={official.name} 
                          style={{ width: '80px', height: '80px', borderRadius: '50%', objectFit: 'cover' }} 
                        />
                        <div style={{ flex: 1 }}>
                          <p style={{ fontSize: '0.875rem', marginBottom: '0.25rem' }}>
                            <strong>Party:</strong> {official.party}
                          </p>
                          <p style={{ fontSize: '0.875rem' }}>
                            <strong>Tenure:</strong> {official.tenure}
                          </p>
                        </div>
                      </div>

                      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '0.5rem', textAlign: 'center', fontSize: '0.75rem', marginBottom: '1rem', paddingTop: '0.75rem', borderTop: '1px solid #e5e7eb' }}>
                        <div>
                          <div style={{ fontWeight: 700, color: '#10b981' }}>{official.completed}</div>
                          <div style={{ color: '#6b7280' }}>Done</div>
                        </div>
                        <div>
                          <div style={{ fontWeight: 700, color: '#3b82f6' }}>{official.inProgress}</div>
                          <div style={{ color: '#6b7280' }}>Progress</div>
                        </div>
                        <div>
                          <div style={{ fontWeight: 700, color: '#ef4444' }}>{official.broken}</div>
                          <div style={{ color: '#6b7280' }}>Broken</div>
                        </div>
                      </div>

                      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', paddingTop: '0.75rem', borderTop: '1px solid #e5e7eb' }}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', fontSize: '0.875rem', color: '#6b7280' }}>
                          <Users size={14} />
                          <span>Community</span>
                        </div>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                          <button className="btn btn-outline" style={{ fontSize: '0.75rem', padding: '0.25rem 0.5rem' }} onClick={(e) => e.stopPropagation()}>
                            Approve
                          </button>
                          <button className="btn btn-danger" style={{ fontSize: '0.75rem', padding: '0.25rem 0.5rem' }} onClick={(e) => e.stopPropagation()}>
                            Disapprove
                          </button>
                        </div>
                      </div>
                    </div>
                  ))}
                </section>
              </>
            )}
        </main>
      </div>

      <footer style={{ marginTop: '4rem', background: '#f3f4f6', textAlign: 'center', padding: '1.5rem', fontSize: '0.875rem', color: '#6b7280' }}>
        © 2025 Social Record Platform — Empowering Open Governance in Bangalore
      </footer>
    </div>
  );
};

export default Homepage;
