import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { ThumbsUp, ThumbsDown, CheckCircle, ExternalLink, Twitter, MessageSquare, Filter } from 'lucide-react';

const ProfilePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const [activeTab, setActiveTab] = useState('profile');
  
  // Use official from location state or create mock data matching spreadsheet
  const [official, setOfficial] = useState(location.state?.official || {
    id: 1,
    name: "Priya Reddy",
    position: "MLA - Bangalore South",
    party: "Indian National Congress",
    constituency: "Bangalore South",
    tenure: "2018–Present",
    dynastyStatus: "Self-Made",
    score: 87,
    image: "https://api.dicebear.com/7.x/avataaars/svg?seed=Priya",
    approvals: 2847,
    disapprovals: 423,
    promises: 24,
    completed: 18,
    inProgress: 4,
    broken: 2,
    lastUpdated: "2 hours ago",
    sources: { myneta: true, eci: true, twitter: true, news: 15, govt: 8 },
    discussions: 156,
    // Profile Details (Tab 1)
    profileDetails: {
      education: { value: "MBA from IIM Bangalore", sourceUrl: "https://myneta.info/priya-reddy" },
      assets: { value: "₹2.5 Crores", sourceUrl: "https://eci.gov.in/affidavit/priya-reddy" },
      liabilities: { value: "₹45 Lakhs", sourceUrl: "https://eci.gov.in/affidavit/priya-reddy" },
      criminalCases: { value: "0 Cases", sourceUrl: "https://myneta.info/priya-reddy" },
      age: { value: "42 years", sourceUrl: "https://myneta.info/priya-reddy" },
      contactEmail: { value: "priya.reddy@karnataka.gov.in", sourceUrl: "https://karnataka.gov.in/officials" }
    }
  });

  const mockPromises = [
    { 
      title: "Build 5 new schools", 
      status: "completed", 
      date: "2024-08", 
      progress: 100,
      sourceUrl: "https://twitter.com/priyareddy/status/123456"
    },
    { 
      title: "Improve road infrastructure (15 km)", 
      status: "in-progress", 
      date: "2024-12", 
      progress: 65,
      sourceUrl: "https://karnataka.gov.in/road-project-2024"
    },
    { 
      title: "Install 50 CCTV cameras", 
      status: "in-progress", 
      date: "2025-03", 
      progress: 40,
      sourceUrl: "https://bbc.com/news/bangalore-cctv-2025"
    },
    { 
      title: "Reduce power cuts by 50%", 
      status: "broken", 
      date: "2023-06", 
      progress: 20,
      sourceUrl: "https://thehindu.com/news/bangalore-power"
    }
  ];

  const activityTimeline = [
    { 
      date: "2025-10-24", 
      event: "Road improvement project announced", 
      source: "Twitter",
      sourceUrl: "https://twitter.com/priyareddy/status/789456"
    },
    { 
      date: "2025-10-22", 
      event: "Constituency meeting on water supply", 
      source: "News",
      sourceUrl: "https://deccanherald.com/bangalore-water-meeting"
    },
    { 
      date: "2025-10-20", 
      event: "Promised 10 new schools", 
      source: "Public Speech",
      sourceUrl: "https://youtube.com/watch?v=xyz123"
    }
  ];

  const comparisons = [
    {
      statement: {
        text: "Complete metro phase 2 by Dec 2024",
        source: "Tweet - Oct 2023",
        sourceUrl: "https://twitter.com/priyareddy/status/456789"
      },
      reality: {
        text: "Metro delayed to June 2025",
        source: "Govt Records - Jan 2025",
        sourceUrl: "https://bmrc.co.in/phase2-delay-notice",
        status: "Delayed"
      }
    },
    {
      statement: {
        text: "Promised 24/7 water supply",
        source: "Rally - Mar 2023",
        sourceUrl: "https://youtube.com/watch?v=rally2023"
      },
      reality: {
        text: "Water supply: 18 hours average",
        source: "BWSSB Data - Oct 2025",
        sourceUrl: "https://bwssb.gov.in/water-supply-data",
        status: "Unfulfilled"
      }
    }
  ];

  return (
    <div style={{ minHeight: '100vh', background: '#f9fafb' }}>
      <Navbar />

      <div style={{ maxWidth: '1280px', margin: '0 auto', padding: '2rem 1rem' }}>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
          
          {/* Profile Header */}
          <div className="card">
            <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
              <img 
                src={official.image} 
                alt={official.name} 
                style={{ width: '80px', height: '80px', borderRadius: '50%', objectFit: 'cover' }}
              />
              <div>
                <h2 style={{ fontSize: '1.5rem', fontWeight: 700, marginBottom: '0.25rem' }}>{official.name}</h2>
                <p style={{ color: '#6b7280' }}>{official.position}</p>
                <p style={{ fontSize: '0.875rem', color: '#6b7280' }}>Party: {official.party}</p>
                <p style={{ fontSize: '0.875rem', color: '#6b7280' }}>Tenure: {official.tenure}</p>
                <p style={{ fontSize: '0.875rem', color: '#6b7280' }}>Dynasty Status: {official.dynastyStatus}</p>
              </div>
            </div>
          </div>

          {/* Tabs */}
          <div style={{ display: 'flex', gap: '0.5rem', borderBottom: '2px solid #e5e7eb', overflowX: 'auto', background: 'white', borderRadius: '1rem 1rem 0 0', padding: '0 1rem' }}>
            {['profile', 'promises', 'activity', 'compare', 'forum'].map(tab => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                style={{
                  padding: '0.75rem 1rem',
                  border: 'none',
                  background: 'none',
                  cursor: 'pointer',
                  fontWeight: 500,
                  textTransform: 'capitalize',
                  whiteSpace: 'nowrap',
                  fontSize: '0.875rem',
                  color: activeTab === tab ? '#1d4ed8' : '#6b7280',
                  borderBottom: activeTab === tab ? '2px solid #1d4ed8' : '2px solid transparent',
                  marginBottom: '-2px',
                  transition: 'all 0.2s'
                }}
              >
                {tab}
              </button>
            ))}
          </div>

          {/* Tab Content */}
          <div className="card">
            {activeTab === 'profile' && (
              <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
                <h3 style={{ fontSize: '1.25rem', fontWeight: 600, marginBottom: '0.5rem' }}>Profile Details</h3>
                <p style={{ fontSize: '0.875rem', color: '#6b7280', marginBottom: '1rem' }}>
                  Static details with source links for verification
                </p>
                
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1rem' }}>
                  {official.profileDetails && Object.entries(official.profileDetails).map(([key, data]) => (
                    <div 
                      key={key} 
                      style={{ 
                        padding: '1rem', 
                        border: '1px solid #e5e7eb', 
                        borderRadius: '0.75rem',
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center'
                      }}
                    >
                      <div style={{ flex: 1 }}>
                        <h4 style={{ fontSize: '0.875rem', fontWeight: 600, color: '#6b7280', textTransform: 'capitalize', marginBottom: '0.25rem' }}>
                          {key.replace(/([A-Z])/g, ' $1').trim()}
                        </h4>
                        <p style={{ fontSize: '1rem', fontWeight: 500, color: '#111827' }}>
                          {data.value}
                        </p>
                      </div>
                      <a 
                        href={data.sourceUrl} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        style={{ 
                          marginLeft: '1rem',
                          padding: '0.5rem',
                          color: '#1d4ed8',
                          textDecoration: 'none',
                          display: 'flex',
                          alignItems: 'center',
                          gap: '0.25rem',
                          fontSize: '0.75rem',
                          border: '1px solid #1d4ed8',
                          borderRadius: '0.375rem',
                          transition: 'all 0.2s'
                        }}
                        onMouseEnter={(e) => {
                          e.currentTarget.style.background = '#1d4ed8';
                          e.currentTarget.style.color = 'white';
                        }}
                        onMouseLeave={(e) => {
                          e.currentTarget.style.background = 'transparent';
                          e.currentTarget.style.color = '#1d4ed8';
                        }}
                      >
                        <ExternalLink size={12} />
                        Source
                      </a>
                    </div>
                  ))}
                </div>

                {/* Summary Score Section */}
                <div className="card" style={{ boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)', marginTop: '1rem' }}>
                  <h3 style={{ fontSize: '1rem', fontWeight: 600, marginBottom: '1rem' }}>Summary Score</h3>
                  <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '1rem' }}>
                    <div style={{ textAlign: 'center' }}>
                      <div style={{ fontSize: '2rem', fontWeight: 700, color: '#10b981' }}>{official.completed}</div>
                      <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>Done</div>
                    </div>
                    <div style={{ textAlign: 'center' }}>
                      <div style={{ fontSize: '2rem', fontWeight: 700, color: '#3b82f6' }}>{official.inProgress}</div>
                      <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>Progress</div>
                    </div>
                    <div style={{ textAlign: 'center' }}>
                      <div style={{ fontSize: '2rem', fontWeight: 700, color: '#ef4444' }}>{official.broken}</div>
                      <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>Broken</div>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'promises' && (
              <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
                  <h3 style={{ fontWeight: 600, fontSize: '1.125rem' }}>Promise Tracking</h3>
                  <button className="btn btn-outline" style={{ fontSize: '0.875rem' }}>
                    <Filter size={16} style={{ marginRight: '0.5rem' }} />
                    Filter
                  </button>
                </div>

                {/* Summary Score at top of Promises tab */}
                <div className="card" style={{ boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)', background: '#f9fafb' }}>
                  <h4 style={{ fontSize: '1rem', fontWeight: 600, marginBottom: '1rem' }}>Summary Score</h4>
                  <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '1rem', textAlign: 'center' }}>
                    <div>
                      <div style={{ fontSize: '2.5rem', fontWeight: 700, color: '#10b981' }}>{official.completed}</div>
                      <div style={{ fontSize: '0.875rem', color: '#6b7280', fontWeight: 500 }}>Done</div>
                    </div>
                    <div>
                      <div style={{ fontSize: '2.5rem', fontWeight: 700, color: '#3b82f6' }}>{official.inProgress}</div>
                      <div style={{ fontSize: '0.875rem', color: '#6b7280', fontWeight: 500 }}>Progress</div>
                    </div>
                    <div>
                      <div style={{ fontSize: '2.5rem', fontWeight: 700, color: '#ef4444' }}>{official.broken}</div>
                      <div style={{ fontSize: '0.875rem', color: '#6b7280', fontWeight: 500 }}>Broken</div>
                    </div>
                  </div>
                </div>
                
                {mockPromises.map((promise, idx) => (
                  <div 
                    key={idx} 
                    style={{ 
                      padding: '1rem', 
                      border: '1px solid #e5e7eb', 
                      borderRadius: '0.75rem',
                      borderLeft: `4px solid ${
                        promise.status === 'completed' ? '#10b981' : 
                        promise.status === 'in-progress' ? '#3b82f6' : '#ef4444'
                      }`
                    }}
                  >
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start', flexWrap: 'wrap', gap: '1rem' }}>
                      <div style={{ flex: 1 }}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '0.5rem' }}>
                          <h4 style={{ fontWeight: 500 }}>{promise.title}</h4>
                          <a 
                            href={promise.sourceUrl} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            style={{ 
                              padding: '0.25rem 0.5rem',
                              color: '#1d4ed8',
                              textDecoration: 'none',
                              display: 'inline-flex',
                              alignItems: 'center',
                              gap: '0.25rem',
                              fontSize: '0.75rem',
                              border: '1px solid #1d4ed8',
                              borderRadius: '0.375rem'
                            }}
                            title="View source"
                          >
                            <ExternalLink size={12} />
                            Source
                          </a>
                        </div>
                        <p style={{ fontSize: '0.875rem', color: '#6b7280', marginTop: '0.25rem' }}>Promised: {promise.date}</p>
                        <div style={{ marginTop: '0.75rem' }}>
                          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.25rem' }}>
                            <span style={{ fontSize: '0.75rem', color: '#6b7280', textTransform: 'capitalize' }}>
                              {promise.status.replace('-', ' ')}
                            </span>
                            <span style={{ fontSize: '0.75rem', fontWeight: 500 }}>{promise.progress}%</span>
                          </div>
                          <div style={{ width: '100%', background: '#e5e7eb', borderRadius: '9999px', height: '0.5rem' }}>
                            <div 
                              style={{
                                height: '0.5rem',
                                borderRadius: '9999px',
                                width: `${promise.progress}%`,
                                background: promise.status === 'completed' ? '#10b981' : 
                                           promise.status === 'in-progress' ? '#3b82f6' : '#ef4444'
                              }}
                            />
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {activeTab === 'activity' && (
              <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
                <h3 style={{ fontWeight: 600, fontSize: '1.125rem', marginBottom: '1rem' }}>Activity Timeline</h3>
                <p style={{ fontSize: '0.875rem', color: '#6b7280', marginBottom: '0.5rem' }}>
                  Chronological list of events/announcements
                </p>
                <div style={{ display: 'flex', flexDirection: 'column', gap: '0.75rem' }}>
                  {activityTimeline.map((activity, idx) => (
                    <div 
                      key={idx}
                      style={{ 
                        padding: '1rem', 
                        border: '1px solid #e5e7eb', 
                        borderRadius: '0.75rem',
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                        gap: '1rem'
                      }}
                    >
                      <div style={{ flex: 1 }}>
                        <p style={{ fontSize: '0.875rem', color: '#374151' }}>
                          <strong>{activity.date}</strong> — {activity.event}
                        </p>
                        <p style={{ fontSize: '0.75rem', color: '#6b7280', marginTop: '0.25rem' }}>
                          Source: {activity.source}
                        </p>
                      </div>
                      <a 
                        href={activity.sourceUrl} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        style={{ 
                          padding: '0.5rem',
                          color: '#1d4ed8',
                          textDecoration: 'none',
                          display: 'flex',
                          alignItems: 'center',
                          gap: '0.25rem',
                          fontSize: '0.75rem',
                          border: '1px solid #1d4ed8',
                          borderRadius: '0.375rem',
                          whiteSpace: 'nowrap'
                        }}
                        title="View source"
                      >
                        <ExternalLink size={12} />
                        Source
                      </a>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {activeTab === 'compare' && (
              <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
                <h3 style={{ fontWeight: 600, fontSize: '1.125rem' }}>Compare: Statements vs Reality</h3>
                <p style={{ fontSize: '0.875rem', color: '#6b7280' }}>
                  Statement vs. Reality table for delayed/broken promises with separate source links
                </p>
                
                {comparisons.map((comparison, idx) => (
                  <div key={idx} style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1.5rem' }}>
                    <div style={{ padding: '1rem', background: '#dbeafe', borderRadius: '0.75rem' }}>
                      <h4 style={{ fontWeight: 500, marginBottom: '0.75rem' }}>What They Said</h4>
                      <div style={{ padding: '0.75rem', background: 'white', border: '1px solid #e5e7eb', borderRadius: '0.5rem' }}>
                        <p style={{ fontSize: '0.875rem', fontWeight: 500 }}>{comparison.statement.text}</p>
                        <p style={{ fontSize: '0.75rem', color: '#6b7280', marginTop: '0.25rem' }}>{comparison.statement.source}</p>
                        <a 
                          href={comparison.statement.sourceUrl} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          style={{ 
                            marginTop: '0.5rem',
                            padding: '0.375rem 0.75rem',
                            color: '#1d4ed8',
                            textDecoration: 'none',
                            display: 'inline-flex',
                            alignItems: 'center',
                            gap: '0.25rem',
                            fontSize: '0.75rem',
                            border: '1px solid #1d4ed8',
                            borderRadius: '0.375rem'
                          }}
                          title="View original statement source"
                        >
                          <ExternalLink size={12} />
                          View Statement Source
                        </a>
                      </div>
                    </div>

                    <div style={{ padding: '1rem', background: '#d1fae5', borderRadius: '0.75rem' }}>
                      <h4 style={{ fontWeight: 500, marginBottom: '0.75rem' }}>Ground Reality</h4>
                      <div style={{ padding: '0.75rem', background: 'white', border: '1px solid #e5e7eb', borderRadius: '0.5rem' }}>
                        <p style={{ fontSize: '0.875rem', fontWeight: 500 }}>{comparison.reality.text}</p>
                        <p style={{ fontSize: '0.75rem', color: '#6b7280', marginTop: '0.25rem' }}>{comparison.reality.source}</p>
                        <div style={{ display: 'flex', gap: '0.5rem', marginTop: '0.5rem', alignItems: 'center' }}>
                          <span className={`badge ${comparison.reality.status === 'Delayed' ? 'badge-warning' : 'badge-danger'}`}>
                            {comparison.reality.status}
                          </span>
                          <a 
                            href={comparison.reality.sourceUrl} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            style={{ 
                              padding: '0.375rem 0.75rem',
                              color: '#1d4ed8',
                              textDecoration: 'none',
                              display: 'inline-flex',
                              alignItems: 'center',
                              gap: '0.25rem',
                              fontSize: '0.75rem',
                              border: '1px solid #1d4ed8',
                              borderRadius: '0.375rem'
                            }}
                            title="View reality verification source"
                          >
                            <ExternalLink size={12} />
                            View Reality Source
                          </a>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {activeTab === 'forum' && (
              <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
                  <h3 style={{ fontWeight: 600, fontSize: '1.125rem' }}>Forum Discussions ({official.discussions})</h3>
                  <button className="btn btn-primary" style={{ fontSize: '0.875rem' }}>
                    <MessageSquare size={16} style={{ marginRight: '0.5rem' }} />
                    New Thread
                  </button>
                </div>
                <p style={{ fontSize: '0.875rem', color: '#6b7280', marginBottom: '0.5rem' }}>
                  User comments and discussion (no inline source links required for user comments)
                </p>

                <div style={{ padding: '1rem', border: '1px solid #e5e7eb', borderRadius: '0.75rem' }}>
                  <div style={{ display: 'flex', alignItems: 'start', gap: '0.75rem' }}>
                    <img 
                      src="https://api.dicebear.com/7.x/avataaars/svg?seed=user1" 
                      style={{ width: '40px', height: '40px', borderRadius: '50%' }}
                      alt="User"
                    />
                    <div style={{ flex: 1 }}>
                      <h4 style={{ fontWeight: 500 }}>Rajesh Kumar</h4>
                      <p style={{ fontSize: '0.875rem', color: '#374151', marginTop: '0.25rem' }}>
                        Road repair work hasn't started yet. Can someone verify budget?
                      </p>
                      <div style={{ display: 'flex', gap: '1rem', marginTop: '0.75rem' }}>
                        <button style={{ display: 'flex', alignItems: 'center', gap: '0.25rem', fontSize: '0.875rem', color: '#6b7280', background: 'none', border: 'none', cursor: 'pointer' }}>
                          <ThumbsUp size={16} />
                          <span>47</span>
                        </button>
                        <button style={{ display: 'flex', alignItems: 'center', gap: '0.25rem', fontSize: '0.875rem', color: '#6b7280', background: 'none', border: 'none', cursor: 'pointer' }}>
                          <MessageSquare size={16} />
                          <span>12 replies</span>
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Action Footer */}
          <div className="card">
            <div style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem', alignItems: 'center', justifyContent: 'space-between' }}>
              <div style={{ display: 'flex', gap: '0.75rem' }}>
                <button className="btn btn-outline" style={{ fontSize: '0.875rem' }}>
                  <ExternalLink size={16} style={{ marginRight: '0.5rem' }} />
                  MyNeta
                </button>
                <button className="btn btn-outline" style={{ fontSize: '0.875rem' }}>
                  <Twitter size={16} style={{ marginRight: '0.5rem' }} />
                  Twitter
                </button>
              </div>
              <div style={{ display: 'flex', gap: '0.5rem' }}>
                <button className="btn" style={{ fontSize: '0.875rem', background: 'transparent', color: '#10b981', border: '1px solid #10b981' }}>
                  <ThumbsUp size={16} style={{ marginRight: '0.25rem' }} />
                  Approve
                </button>
                <button className="btn btn-danger" style={{ fontSize: '0.875rem' }}>
                  <ThumbsDown size={16} style={{ marginRight: '0.25rem' }} />
                  Disapprove
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
