import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Search } from 'lucide-react';

const Navbar = ({ onSearch }) => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (e) => {
    e.preventDefault();
    if (onSearch) {
      onSearch(searchQuery);
    }
  };

  return (
    <nav>
      <div className="nav-container" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexWrap: 'wrap', gap: '1rem' }}>
        {/* App Logo/Title (Home) */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <Link to="/" style={{ textDecoration: 'none' }}>
            <h1>Social Record Platform</h1>
          </Link>
        </div>

        {/* Search Bar */}
        <form onSubmit={handleSearch} style={{ flex: '1', maxWidth: '500px', position: 'relative' }}>
          <div style={{ position: 'relative' }}>
            <Search 
              size={18} 
              style={{ 
                position: 'absolute', 
                left: '12px', 
                top: '50%', 
                transform: 'translateY(-50%)',
                color: '#9ca3af'
              }} 
            />
            <input
              type="text"
              placeholder="Search officials, constituency, party..."
              className="input"
              value={searchQuery}
              onChange={(e) => {
                setSearchQuery(e.target.value);
                if (onSearch) onSearch(e.target.value);
              }}
              style={{ 
                width: '100%', 
                paddingLeft: '40px',
                margin: 0
              }}
            />
          </div>
        </form>

        {/* Forum, Login */}
        <ul className="nav-links" style={{ margin: 0 }}>
          <li><Link to="/">Forum</Link></li>
          <li><Link to="/">Login</Link></li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
