            assets: { value: "₹5.2 Crores", sourceUrl: "https://eci.gov.in/affidavit/priya-reddy" },
            liabilities: { value: "₹1.2 Crores", sourceUrl: "https://eci.gov.in/affidavit/priya-reddy" },
            criminalCases: { value: "0 Cases", sourceUrl: "https://myneta.info/priya-reddy" },
