import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ThumbsUp, ThumbsDown, ExternalLink, Twitter } from 'lucide-react';
import axios from 'axios';

const ProfilePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('profile');
  const [official, setOfficial] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchOfficialProfile();
  }, [id]);

  const fetchOfficialProfile = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`http://localhost:5001/api/officials/${id}`);
      setOfficial(response.data);
      setError(null);
    } catch (err) {
      console.error('Error fetching official:', err);
      setError('Failed to load profile data');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div style={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', background: '#f9fafb' }}>
        <div style={{ textAlign: 'center' }}>
          <div style={{ fontSize: '1.5rem', fontWeight: 600, marginBottom: '0.5rem' }}>Loading Profile...</div>
          <p style={{ color: '#6b7280' }}>Fetching comprehensive data from verified sources</p>
        </div>
      </div>
    );
  }

  if (error || !official) {
    return (
      <div style={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', background: '#f9fafb' }}>
        <div style={{ textAlign: 'center' }}>
          <div style={{ fontSize: '1.5rem', fontWeight: 600, color: '#ef4444', marginBottom: '0.5rem' }}>Error</div>
          <p style={{ color: '#6b7280' }}>{error || 'Official not found'}</p>
          <button 
            onClick={() => navigate('/')} 
            className="btn btn-primary" 
            style={{ marginTop: '1rem' }}
          >
            Back to Home
          </button>
        </div>
      </div>
    );
  }

  const profile = official.profileOverview || {};

  const renderSimpleField = (label, value) => {
    if (!value || value === 'N/A') return null;
    return (
      <div style={{ marginBottom: '0.75rem' }}>
        <span style={{ fontWeight: 600, color: '#374151', fontSize: '0.875rem' }}>{label}:</span>{' '}
        <span style={{ color: '#6b7280', fontSize: '0.875rem' }}>{value}</span>
      </div>
    );
  };

  return (
    <div style={{ minHeight: '100vh', background: '#f9fafb' }}>
      <div style={{ maxWidth: '1280px', margin: '0 auto', padding: '2rem 1rem' }}>
        <div className="card" style={{ marginBottom: '1.5rem' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '1.5rem' }}>
            <img src={official.image} alt={official.name} style={{ width: '100px', height: '100px', borderRadius: '50%', objectFit: 'cover', border: '3px solid #e5e7eb' }} />
            <div style={{ flex: 1 }}>
              <h1 style={{ fontSize: '2rem', fontWeight: 700, marginBottom: '0.5rem', color: '#111827' }}>{official.name}</h1>
              <p style={{ fontSize: '1.125rem', color: '#6b7280', marginBottom: '0.5rem' }}>{official.position}</p>
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem', fontSize: '0.875rem', color: '#6b7280' }}>
                <span><strong>Party:</strong> {official.party}</span>
                <span><strong>Constituency:</strong> {official.constituency}</span>
              </div>
            </div>
          </div>
        </div>
        <div style={{ display: 'flex', gap: '0.5rem', borderBottom: '2px solid #e5e7eb', background: 'white', borderRadius: '0.75rem 0.75rem 0 0', padding: '0 1rem', marginBottom: '0' }}>
          {[
            { id: 'profile', label: ' Profile' },
            { id: 'promises', label: ' Promises' },
            { id: 'activity', label: ' Activity' },
            { id: 'compare', label: ' Compare' },
            { id: 'forum', label: ' Forum' }
          ].map(tab => (
            <button key={tab.id} onClick={() => setActiveTab(tab.id)} style={{ padding: '1rem 1.5rem', border: 'none', background: 'none', cursor: 'pointer', fontWeight: 600, whiteSpace: 'nowrap', fontSize: '0.95rem', color: activeTab === tab.id ? '#3b82f6' : '#6b7280', borderBottom: activeTab === tab.id ? '3px solid #3b82f6' : '3px solid transparent', marginBottom: '-2px', transition: 'all 0.2s' }}>{tab.label}</button>
          ))}
        </div>
        <div className="card" style={{ borderRadius: '0 0 0.75rem 0.75rem', marginTop: '0' }}>
          {activeTab === 'profile' && profile && (
            <div style={{ display: 'flex', flexDirection: 'column', gap: '2.5rem' }}>
              <div><h2 style={{ fontSize: '1.5rem', fontWeight: 700, marginBottom: '0.5rem', color: '#111827' }}>Profile Overview</h2><p style={{ fontSize: '0.95rem', color: '#6b7280' }}>Comprehensive profile with 8 standardized sections</p></div>
              {profile.currentOfficeParty && <div><h3 style={{ fontSize: '1.1rem', fontWeight: 600, marginBottom: '1rem', color: '#1d4ed8', borderLeft: '4px solid #1d4ed8', paddingLeft: '1rem' }}>1. Current Office & Party</h3><div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '1rem' }}>{renderProfileField('Position', profile.currentOfficeParty?.position)}{renderProfileField('Party & Role', profile.currentOfficeParty?.party)}{renderProfileField('Constituency', profile.currentOfficeParty?.constituency)}</div></div>}
              {profile.education && <div><h3 style={{ fontSize: '1.1rem', fontWeight: 600, marginBottom: '1rem', color: '#1d4ed8', borderLeft: '4px solid #1d4ed8', paddingLeft: '1rem' }}>2. Educational Status</h3><div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '1rem' }}>{renderProfileField('Education', profile.education)}</div></div>}
              {profile.assetsFinancials && <div><h3 style={{ fontSize: '1.1rem', fontWeight: 600, marginBottom: '1rem', color: '#1d4ed8', borderLeft: '4px solid #1d4ed8', paddingLeft: '1rem' }}>3. Assets & Financials</h3><div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '1rem' }}>{renderProfileField('Total Assets', profile.assetsFinancials?.totalAssets)}{renderProfileField('Source of Wealth', profile.assetsFinancials?.sourceOfWealth)}{renderProfileField('Liabilities', profile.assetsFinancials?.liabilities)}</div></div>}
              {profile.criminalCases && <div><h3 style={{ fontSize: '1.1rem', fontWeight: 600, marginBottom: '1rem', color: '#1d4ed8', borderLeft: '4px solid #1d4ed8', paddingLeft: '1rem' }}>4. Criminal Cases</h3><div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '1rem' }}>{renderProfileField('Total Cases', profile.criminalCases?.totalCases)}{renderProfileField('Serious IPC Charges', profile.criminalCases?.seriousCharges)}</div></div>}
              {profile.politicalBackground && <div><h3 style={{ fontSize: '1.1rem', fontWeight: 600, marginBottom: '1rem', color: '#1d4ed8', borderLeft: '4px solid #1d4ed8', paddingLeft: '1rem' }}>5. Political Background</h3><div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '1rem' }}>{renderProfileField('Dynasty Status', profile.politicalBackground?.dynastyStatus)}{renderProfileField('Career Highlight', profile.politicalBackground?.careerHighlight)}</div></div>}
              {profile.promises && <div><h3 style={{ fontSize: '1.1rem', fontWeight: 600, marginBottom: '1rem', color: '#1d4ed8', borderLeft: '4px solid #1d4ed8', paddingLeft: '1rem' }}>6. Promises & Credibility</h3><div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '1rem' }}>{renderProfileField('Key Promises', profile.promises?.keyPromises)}{renderProfileField('Current Focus', profile.promises?.currentFocus)}</div></div>}
              {profile.affiliatedCompanies && <div><h3 style={{ fontSize: '1.1rem', fontWeight: 600, marginBottom: '1rem', color: '#1d4ed8', borderLeft: '4px solid #1d4ed8', paddingLeft: '1rem' }}>7. Affiliated Companies</h3><div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '1rem' }}>{renderProfileField('Primary Company', profile.affiliatedCompanies?.primaryCompany)}{renderProfileField('Market Cap', profile.affiliatedCompanies?.marketCap)}{renderProfileField('Family Shareholding', profile.affiliatedCompanies?.familyShareholding)}{renderProfileField('Other Ventures', profile.affiliatedCompanies?.otherCompanies)}</div></div>}
              {profile.relativesInCompanies && <div><h3 style={{ fontSize: '1.1rem', fontWeight: 600, marginBottom: '1rem', color: '#1d4ed8', borderLeft: '4px solid #1d4ed8', paddingLeft: '1rem' }}>8. Relatives in Companies</h3><div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '1rem' }}>{renderProfileField('Spouse', profile.relativesInCompanies?.spouse)}{renderProfileField('Children', profile.relativesInCompanies?.children)}{renderProfileField('Other Relatives', profile.relativesInCompanies?.otherRelatives)}{renderProfileField('Family Control', profile.relativesInCompanies?.familyControl)}</div></div>}
              <div style={{ padding: '1.5rem', background: 'linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)', borderRadius: '0.75rem', border: '2px solid #0ea5e9' }}><h3 style={{ fontSize: '1.1rem', fontWeight: 600, marginBottom: '1.5rem', color: '#0c4a6e' }}>Performance Summary</h3><div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '1.5rem', textAlign: 'center' }}><div><div style={{ fontSize: '3rem', fontWeight: 700, color: '#10b981' }}>{official.completed || 0}</div><div style={{ fontSize: '0.875rem', color: '#6b7280', fontWeight: 600 }}>Completed</div></div><div><div style={{ fontSize: '3rem', fontWeight: 700, color: '#3b82f6' }}>{official.inProgress || 0}</div><div style={{ fontSize: '0.875rem', color: '#6b7280', fontWeight: 600 }}>In Progress</div></div><div><div style={{ fontSize: '3rem', fontWeight: 700, color: '#ef4444' }}>{official.broken || 0}</div><div style={{ fontSize: '0.875rem', color: '#6b7280', fontWeight: 600 }}>Broken</div></div></div></div>
            </div>
          )}
          {activeTab !== 'profile' && (
            <div style={{ textAlign: 'center', padding: '4rem 2rem' }}><h3 style={{ fontSize: '1.5rem', fontWeight: 600, color: '#374151', marginBottom: '0.5rem', textTransform: 'capitalize' }}>{activeTab} Tab</h3><p style={{ color: '#9ca3af', fontSize: '1rem' }}>Feature coming soon...</p></div>
          )}
        </div>
        <div className="card" style={{ marginTop: '1.5rem' }}><div style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem', alignItems: 'center', justifyContent: 'space-between' }}><div style={{ display: 'flex', gap: '0.75rem' }}><button className="btn btn-outline" style={{ fontSize: '0.875rem' }}><ExternalLink size={16} style={{ marginRight: '0.5rem' }} />MyNeta</button><button className="btn btn-outline" style={{ fontSize: '0.875rem' }}><Twitter size={16} style={{ marginRight: '0.5rem' }} />Twitter</button></div><div style={{ display: 'flex', gap: '0.75rem' }}><button className="btn" style={{ fontSize: '0.875rem', background: 'transparent', color: '#10b981', border: '2px solid #10b981' }}><ThumbsUp size={16} style={{ marginRight: '0.5rem' }} />Approve ({official.approvals || 0})</button><button className="btn" style={{ fontSize: '0.875rem', background: '#ef4444', color: 'white', border: '2px solid #ef4444' }}><ThumbsDown size={16} style={{ marginRight: '0.5rem' }} />Disapprove ({official.disapprovals || 0})</button></div></div></div>
      </div>
    </div>
  );
};

export default ProfilePage;
